import loading from '../components/loading';
export default {
  components: {
    loading: loading
  },
  data: function data() {
    return {
      ruleForm: {
        name: '',
        desc: ''
      },
      rules: {
        name: [{
          required: true,
          message: '请输入留言标题',
          trigger: 'blur'
        }, {
          min: 3,
          max: 15,
          message: '长度在 3 到 15 个字符',
          trigger: 'blur'
        }],
        desc: [{
          required: false,
          message: '请输入留言内容',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    submitForm: function submitForm(formName) {
      var _this = this;

      this.$refs[formName].validate(function (valid) {
        if (valid) {
          // alert('submit!');
          _this.load = true;
          console.log(_this.ruleForm);

          _this.$api.user.bbs({
            email: sessionStorage.getItem('username'),
            title: _this.ruleForm.name,
            message: _this.ruleForm.desc
          }).then(function (res) {
            console.log(res);
            _this.load = false;

            if (res.data.status == '0') {
              _this.$alert('您的留言已收到，我们将尽快处理您的问题，感谢您的反馈！');
            } else {
              _this.$message.error(res.data.message);
            }
          });
        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },
    resetForm: function resetForm(formName) {
      this.$refs[formName].resetFields();
    }
  }
};
