import { setTimeout } from 'timers';
import loading from '../components/loading';
import liked from "../components/liked";
import resetpwd from "../components/resetpwd";
import bbs from "../components/bbs";
import donote from "../components/donote";
import rank from "../components/rank";

var crypto = require("crypto");

export default {
  components: {
    liked: liked,
    loading: loading,
    resetpwd: resetpwd,
    bbs: bbs,
    donote: donote,
    rank: rank
  },
  data: function data() {
    var checkStock = function checkStock(rule, value, callback) {
      if (!value) {
        return callback(new Error('不能为空'));
      }

      setTimeout(function () {
        callback();
      }, 1000);
    };

    return {
      Say: "自定义预测时间",
      showpickdate: 0,
      pickerOptions: {
        disabledDate: function disabledDate(time) {
          return time.getTime() > _Date$now();
        },
        shortcuts: [{
          text: '今天',
          onClick: function onClick(picker) {
            picker.$emit('pick', new Date());
          }
        }, {
          text: '昨天',
          onClick: function onClick(picker) {
            var date = new Date();
            date.setTime(date.getTime() - 3600 * 1000 * 24);
            picker.$emit('pick', date);
          }
        }, {
          text: '一周前',
          onClick: function onClick(picker) {
            var date = new Date();
            date.setTime(date.getTime() - 3600 * 1000 * 24 * 7);
            picker.$emit('pick', date);
          }
        }]
      },
      chooseday: new Date(),
      ruleForm: {
        stock: ''
      },
      rules: {
        stock: [{
          validator: checkStock,
          trigger: 'blur'
        }]
      },
      //   email:null,
      username: null,
      load: false,
      dialogVisible: false,
      resestpwdVisible: false,
      bbsVisible: false,
      donateVisible: false,
      active: '',
      maps: [],
      mapsId: []
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.username = sessionStorage.getItem('username');
    console.log(sessionStorage.getItem('username'));
    this.$nextTick(function () {
      _this.chooseday = _this.formatDate(_this.chooseday), _this.getAllStocks();
    });
  },
  methods: {
    querySearch: function querySearch(queryString, cb) {
      var maps = this.maps;
      var mapsId = this.mapsId;
      var numReg = /^[0-9]+$/;
      var results;
      var numRe = new RegExp(numReg);

      if (!numRe.test(this.ruleForm.stock)) {
        // this.$alert("query by name!")
        results = queryString ? maps.filter(this.createFilter(queryString)) : maps;
      } else {
        results = queryString ? mapsId.filter(this.createFilterId(queryString)) : mapsId;
      } // 调用 callback 返回建议列表的数据


      cb(results);
    },
    createFilter: function createFilter(queryString) {
      return function (map) {
        return map.value.toLowerCase().indexOf(queryString.toLowerCase()) === 0;
      };
    },
    createFilterId: function createFilterId(queryString) {
      return function (map) {
        return map.value.toLowerCase().indexOf(queryString.toLowerCase()) === 0;
      };
    },
    handleSelect: function handleSelect(item) {
      console.log(item);
    },
    getAllStocks: function getAllStocks() {
      var _this2 = this;

      this.$api.stock.getAll({}).then(function (_ref) {
        var data = _ref.data;

        if (data.status == '0') {
          var jsonObj = JSON.parse(data.content);

          for (var i = 0; i < jsonObj.length; i++) {
            var record = jsonObj[i];
            var stockId = record['stockId'];
            var stockName = record['stockName'];

            _this2.maps.push({
              value: stockName,
              label: stockName,
              key: stockId
            });

            _this2.mapsId.push({
              value: stockId
            });
          }
        } else {
          _this2.$message.error(data.message);
        }
      });
    },
    formatDate: function formatDate(date) {
      var y = date.getFullYear();
      var m = date.getMonth() + 1;
      m = m < 10 ? '0' + m : m;
      var d = date.getDate();
      d = d < 10 ? '0' + d : d;
      return y + '-' + m + '-' + d;
    },
    formatTime: function formatTime(time) {
      this.chooseday = time; //date是绑定的值
    },
    handleClose: function handleClose(done) {
      this.$confirm('确认关闭？').then(function (_) {
        done();
      }).catch(function (_) {});
    },
    clickDate: function clickDate() {
      if (this.showpickdate == 0) {
        this.showpickdate = 1;
        this.Say = "使用默认时间";
      } else {
        this.showpickdate = 0;
        this.Say = "自定义预测时间";
      }
    },
    submitForm: function submitForm(formName) {
      var _this3 = this;

      //this.$alert(this.chooseday);
      // this.load=true
      var md5 = crypto.createHash("md5");
      this.$refs[formName].validate(function (valid) {
        if (valid) {
          _this3.load = true;
          console.log(_this3.ruleForm);
          console.log(_this3.chooseday);

          _this3.$api.stockPredict.getByStockId({
            //post请求发送到searchStock
            stockId: _this3.ruleForm.stock,
            // stock:md5.update(this.ruleForm.stock).digest("hex"),
            // stock:this.$getCode(this.ruleForm.stock),
            pickdate: _this3.chooseday
          }).then(function (res) {
            console.log(res);
            _this3.load = false;

            if (res.data.status == '0') {
              var stock = JSON.parse(res.data.content); // this.$alert(res.data.content);

              _this3.$message.success("搜索成功");

              var stockId = stock.stockId;
              var stockName = stock.stockName;
              var isUp = stock.isUp;
              var isUpStop = stock.isUpStop;
              var stockMarket = stock.stockMarket; // let stockForecast=stock.stockForecast;

              var percentage = stock.percentage;

              _this3.$router.push({
                name: 'showstock',
                query: {
                  stockId: stockId,
                  stockName: stockName,
                  stockMarket: stockMarket,
                  isUp: isUp,
                  isUpStop: isUpStop,
                  percentage: percentage
                }
              });
            } else {
              _this3.$message.error(res.data.message);
            }
          });
        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },
    logout: function logout() {
      this.$store.dispatch('UserLogout');

      if (!this.$store.state.token) {
        this.$router.go(0);
      } else {
        this.$message.error('退出失败');
      }
    }
  }
};
