import loading from '../components/loading'
import liked from "../components/liked";
import resetpwd from "../components/resetpwd";
import bbs from "../components/bbs";
import favorite from "../components/favorite";
import donote from "../components/donote";


export default {
    components: {loading,liked,resetpwd,bbs,favorite,donote},
    //            var stock=this.$route.params.stock;
    data() {
        return {
            stockId:'',
            stockName:'',
            stockMarket:'',
            isUp:1,//1表示涨，0表示跌
            isUpStop:0,//0表示不涨停，1表示涨停
            percentage:"",
            username:null,
            dialogVisible: false,
            resestpwdVisible:false,
            bbsVisible:false,
            favoriteVisible:false,
            donateVisible:false,
            maps:[]
        }
    },
        mounted:function(){
            this.$nextTick(()=>{
                this.getAllStocks()
             //   this.getUserStock()
            })
            this.username=sessionStorage.getItem('username');
            this.stockId=this.$route.query.stockId;
            console.log(this.$route.query.szStockId);
            this.stockName = this.$route.query.stockName;
            this.percentage=this.$route.query.percentage;
            this.stockMarket=this.$route.query.stockMarket;
            this.isUp=this.$route.query.isUp;
            this.isUpStop=this.$route.query.isUpStop;
        },
    methods:{
        getAllStocks(){
            this.$api.stock.getAll({}).then(({data})=>{
                if(data.status=='0'){
                    var jsonObj=JSON.parse(data.content);
                    for (var i = 0; i < jsonObj.length; i++) {
                        var record = jsonObj[i];
                        var stockId = record['stockId'];
                        var stockName = record['stockName'];
                        this.maps.push({
                            label: stockName,
                            key: stockId,
                        });
                    }

                }else {
                    this.$message.error(data.message);
                }
            })
        },
        addLikedStock(stockId)
        {
            //let stockNum=[];
            //stockMap.push({stockId});
            //add likedStock
            this.$api.stock.addLikedStock({//post请求发送到searchStock
                stockNum:stockId+",",
                email:sessionStorage.getItem('username')
            }).then((res)=>{
                if(res.data.status=='0'){
                    this.$alert("添加成功");

                }else {
                    this.$message.error(res.data.message);
                }

            })
        },
        delLikedStock(stockId)
        {
            //del likedStock
            this.$api.stock.delLikedStock({//post请求发送到searchStock
                stockNum:stockId+"",
                email:sessionStorage.getItem('username')
            }).then((res)=>{
                if(res.data.status=='0'){
                    this.$alert("删除成功");

                }else {
                    this.$message.error(res.data.message);
                }

            })
        },
        handleClose(done) {
            this.$confirm('确认关闭？')
                .then(_ => {
                    done();
                })
                .catch(_ => {});
        },
        back(){
            this.$router.go(-1);//返回上一层
        },
        logout(){
            this.$store.dispatch('UserLogout')
            if(! this.$store.state.token){

                this.$router.go(0)
            }else{
                this.$message.error('退出失败');
            }

        }
    }




}
