@media (max-width: 2000px)  {
    .show{
        position:relative;
        width: 100%;
        height: 100%;
        background-image: url(../common/img/background.jpg);
        background-size:100% 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        color: #fff;
        .logo{
            //  margin-bottom:10%;
            margin-left:50%;
            img{
                width: 80%;
            }
        }
        .header{
            position:absolute;
            right:10px;
            top:5px;
        }

        .show-echarts {
            width: 30%;
            border-radius: 30px;
            box-shadow: 0 2px 4px rgba(0, 0, 0, .12), 0 0 6px rgba(0, 0, 0, .04);
            padding: 10px;
            margin-top: -10px;
            margin-left: 45%;

            .el-input {
                // margin-bottom: 20px;
                .el-input__inner {
                    border-radius: 30px !important;
                    text-align: center;
                }
            }

            .stock-class-up {
                border-color: #f56c6c;
                display: flex;
                background-color: #ffffff;
                //opacity: 0.8;
                width: 100%;
                height: 70%;
                color: #f56c6c;


                .stockName-class-up {
                    flex: 1;
                    width: 30%;
                    font-size: 2.5rem;
                    font-weight: bold;
                    font-family: pingfang SC;
                    text-align: center;
                    line-height: 6rem;

                    .stockInfo-class-up {
                        font-size: 1.5rem;
                        font-weight: bolder;

                    }
                }

                .stockForecast-class-up {
                    display: inline;
                    width: 55%;
                    margin-right: 10%;
                    text-align: right;
                    font-size: 2.5rem;
                    font-weight: bold;
                    line-height: 10rem;
                    font-family: pingfang SC;

                    .stopUp {
                    }

                }
            }

            .stock-class-down {
                border-color: #67C23A;
                display: flex;
                background-color: #ffffff;
                //opacity: 0.8;
                width: 100%;
                height: 70%;
                color: #67C23A;


                .stockName-class-down {
                    flex: 1;
                    width: 30%;
                    font-size: 2.5rem;
                    font-weight: bold;
                    font-family: pingfang SC;
                    text-align: center;
                    line-height: 6rem;

                    .stockInfo-class-up {
                        font-size: 1.5rem;
                        font-weight: bolder;

                    }
                }

                .stockForecast-class-down {
                    display: inline;
                    width: 55%;
                    margin-right: 10%;
                    text-align: right;
                    font-size: 2.5rem;
                    font-weight: bold;
                    line-height: 10rem;
                    font-family: pingfang SC;

                    .stopUp {
                    }

                }
            }

            .header-class {
                height: 7%;

                .el-button {
                    background: rgb(222, 222, 222);
                    color: #fff;
                    border: 0;
                    margin-left: 90%;
                }
            }

            .register {
                display: inline;
                // text-align: right;
                margin-left: 50px;
                color: #f3a407;

            }

        }

    }
}
